# Create directory by name "< extensions > files" where directory_name is name of directory to be created
def create_extension_dir(directory_name):
    if not os.path.exists(directory_name):
        os.mkdir(directory_name)


# find all the files of extension 'ext' in the destination_root directory
def find_files( destination_root, ext):
    return glob.glob(destination_root+'*%s'%ext)


# it move all files in destination directory
def moveFiles(all_files,destination_directory):
	for file in all_files:
		shutil.move(file,destination_directory)
	return 

# its work to find all files with extension ext 
def doAllWork(destination_root):
	for ext in files_extension:
		# find_files function list of files with extension ext with full path
		all_files = find_files(destination_root,ext)
		# if files exist with extension ext 
		if  all_files:
			create_extension_dir(ext+" files")
			moveFiles(all_files,destination_root + "%s files"%ext)
		# if not any file exist extension ext
		else:
			pass


